import com.eviware.soapui.support.GroovyUtils

def groovyUtils = new GroovyUtils(context)
def requestXmlHolder = groovyUtils.getXmlHolder(mockRequest.getRequestContent())
def type = requestXmlHolder["//DestinationContext[1]/Type"]
def quality = requestXmlHolder["//DestinationContext[1]/Quality"]

log.info type
log.info quality

if (type == 'invalid' || quality == 'invalid') {
	return 'Response error'
}
else {
	return 'Response OK'
}
